import React from "react";
import {
  Accordion,
  AccordionItem,
  AccordionItemHeading,
  AccordionItemButton,
  AccordionItemPanel,
} from "react-accessible-accordion";
import "./forecast.css";

const WEEK_DAYS = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];

const Forecast = ({ data }) => {
  const dayInAWeek = new Date().getDay();
  const forecastDays = WEEK_DAYS.slice(dayInAWeek, WEEK_DAYS.length).concat(WEEK_DAYS.slice(0, dayInAWeek));
  const numOfDays = 5;
  const numOfIntervalsPerDay = 8;

  return (
    <>
      <label className="titleDL">Daily</label>
      <Accordion allowZeroExpanded>
        {forecastDays.slice(0, numOfDays).map((day, idx) => {
          const startIndex = idx * numOfIntervalsPerDay;
          const endIndex = startIndex + numOfIntervalsPerDay;

          return (
            <AccordionItem key={idx}>
              <AccordionItemHeading>
                <AccordionItemButton>
                  <div className="daily-item">
                    <img src={`icons/${data.list[startIndex].weather[0].icon}.png`} className="icon-small" alt="weather" />
                    <label className="day">{day}</label>
                    <label className="description">{data.list[startIndex].weather[0].description}</label>
                    <label className="min-max">{Math.round(data.list.slice(startIndex, endIndex).reduce((acc, item) => Math.max(acc, item.main.temp_max), -Infinity))}°F / {Math.round(data.list.slice(startIndex, endIndex).reduce((acc, item) => Math.min(acc, item.main.temp_min), Infinity))}°F</label>
                  </div>
                </AccordionItemButton>
              </AccordionItemHeading>
              <AccordionItemPanel>
                <div className="daily-details-grid">
                  <div className="daily-details-grid-item">
                    <label>Feels like:</label>
                    <label>{Math.round(data.list[endIndex].main.feels_like)}°F</label>
                  </div>
                  <div className="daily-details-grid-item">
                    <label>Wind speed:</label>
                    <label>{data.list[startIndex].wind.speed} mph</label>
                  </div>
                  <div className="daily-details-grid-item">
                    <label>Humidity:</label>
                    <label>{data.list[startIndex].main.humidity}%</label>
                  </div>
                  <div className="daily-details-grid-item">
                    <label>Pressure:</label>
                    <label>{data.list[startIndex].main.pressure} hPa</label>
                  </div>
                  <div className="daily-details-grid-item">
                    <label>Clouds:</label>
                    <label>{data.list[startIndex].clouds.all}%</label>
                  </div>
                </div>
              </AccordionItemPanel>
            </AccordionItem>
          );
        })}
      </Accordion>
    </>
  );
};

export default Forecast;
